import React from 'react';

export default function Widget() {
  return (
    <div className="bg-blue-100 min-h-screen flex items-center justify-center p-4">
      <div className="bg-white rounded-lg shadow-lg p-8 max-w-4xl w-full">
        <header className="flex justify-between items-center mb-6">
          <h1 className="text-xl font-semibold">Checkout</h1>
          <nav>
            <a href="#" className="text-blue-500 mx-2">View Plans</a>
            <a href="#" className="text-blue-500 mx-2">Checkout</a>
            <a href="#" className="text-blue-500 mx-2">Confirmation</a>
          </nav>
        </header>

        <div className="grid grid-cols-2 gap-8">
          <div>
            <h2 className="text-lg font-semibold mb-4">Personal Details</h2>
            <form>
              <div className="grid grid-cols-2 gap-4 mb-4">
                <input type="text" placeholder="First name" className="border p-2 rounded w-full" />
                <input type="text" placeholder="Last name" className="border p-2 rounded w-full" />
              </div>
              <input type="email" placeholder="Email address" className="border p-2 rounded w-full mb-4" />
              <input type="password" placeholder="Password" className="border p-2 rounded w-full mb-4" />
              
              <h2 className="text-lg font-semibold mb-4">Payment Method</h2>
              <div className="flex items-center mb-4">
                <input type="radio" id="wallet" name="payment" className="mr-2" />
                <label htmlFor="wallet" className="mr-4">Wallet Payment</label>
                <input type="radio" id="card" name="payment" className="mr-2" />
                <label htmlFor="card">Payment with Cards</label>
              </div>
            </form>
          </div>

          <div>
            <h2 className="text-lg font-semibold mb-4">Card Details</h2>
            <form>
              <input type="text" placeholder="Name on card" className="border p-2 rounded w-full mb-4" />
              <input type="text" placeholder="Card number" className="border p-2 rounded w-full mb-4" />
              <div className="grid grid-cols-3 gap-4 mb-4">
                <input type="text" placeholder="MM/YY" className="border p-2 rounded w-full" />
                <input type="text" placeholder="CVV" className="border p-2 rounded w-full" />
              </div>
              <div className="mb-4">
                <label htmlFor="promo" className="block mb-2">Do you have a promo code?</label>
                <input type="text" id="promo" className="border p-2 rounded w-3/4 mr-2" />
                <button className="bg-blue-500 text-white px-4 py-2 rounded">Apply</button>
              </div>
            </form>
          </div>
        </div>

        <div className="flex justify-end mt-4">
          <button className="bg-blue-500 text-white px-6 py-2 rounded">Checkout</button>
        </div>
      </div>
    </div>
  );
}
